import React, { Component } from 'react'
import axios from "axios"
//import { backend } from '../App'

export default class ShowEstudiantes extends Component {
    state={
        users: []
    }
    
    async componentDidMount() {
        const res= await axios.get('http://localhost:8080/api/estudiante');
        this.setState({users: res.data})
        console.log(this.state.users)
    }
    render() {
        return (
            <div className="row">
                <div className="row md-4">
                    
                    
                </div>
                <div className="col-md-8">
                    <ul className= "list-group">
                        {
                            this.state.users.map( user => <li className="list-group-item list-group-item-action" key={user.idestudiante}>
                                {user.nombre}
                            </li>)
                        }


                    </ul>
                </div>


            </div>
        )
    }


   
}
